# get player uuid
VAR response := SENDHTTPREQUEST("xml/device_description.xml");
VAR content := PARSEJSON(response, "Content");
VAR playerUuid := PARSETEXT(response, "<UDN>uuid:", "</UDN>");
#VAR playerUuid := PARSEXML(content, "./root[1]/device[1]/UDN[1]");

IF(ISNULL(playerUuid))
    ADDERROR("Failed to get player UUID");
    RETURN(-1);
END
######################

######################
# fetch playlists
VAR headers := "SOAPACTION: \"urn:schemas-upnp-org:service:ContentDirectory:1#Browse\"";
VAR body := "<s:Envelope s:encodingStyle=\"http://schemas.xmlsoap.org/soap/encoding/\" xmlns:s=\"http://schemas.xmlsoap.org/soap/envelope/\"><s:Body><u:Browse xmlns:u=\"urn:schemas-upnp-org:service:ContentDirectory:1\"><ObjectID>SQ:</ObjectID><BrowseFlag>BrowseDirectChildren</BrowseFlag><Filter></Filter><StartingIndex>0</StartingIndex><RequestedCount>100</RequestedCount><SortCriteria></SortCriteria></u:Browse></s:Body></s:Envelope>";

response := SENDHTTPREQUEST("MediaServer/ContentDirectory/Control", "POST", body, headers);
content := PARSEJSON(response, "Content");
VAR soapXml := UNESCAPEXML(PARSETEXT(content, "<Result>", "</Result>"));
VAR playlistId := PARSEXML(soapXml, "//DIDL-Lite:container[dc:title='" + playlist_name + "']/@id");
VAR playlistUri := PARSEXML(soapXml, "//DIDL-Lite:container[dc:title='" + playlist_name + "']/DIDL-Lite:res");
######################

IF(ISNULL(playlistId) OR ISNULL(playlistUri))
    ADDERROR("Unable to find playlist with name '" + playlist_name + "'");
    RETURN(-2);
END

